import React, { Component } from 'react';
import { Button } from './Button';

export class Form extends Component{
  constructor(props){
    super(props);
    this.state = {
      id: this.props.id || '',
      name: this.props.name || '',
      model: this.props.model || '',
    }
    this.onChangeInput = this.onChangeInput.bind(this);
    this.handleBtn = this.handleBtn.bind(this);
  }

  onChangeInput = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
  }

  handleBtn = () => {
    if(this.props.title === "Add") {
      this.props.addItem({ id: new Date(), ...this.state });
    } else {
      console.log('edit')
      this.props.editItem(this.state);
    }
  }

  render() {
    return (
      <div>
        <form>
          <label>Name:</label>
          <input type="text" placeholder="name" name="name" value={this.state.name} onChange={this.onChangeInput} />
          <label>Model:</label>
          <input type="text" placeholder="model" name="model" value={this.state.model} onChange={this.onChangeInput} />
        </form>
        <Button labelBtn="Save" handleBtn={this.handleBtn} />
      </div>
    )
  }

}
