import React, { Component } from 'react';
import './App.css';
import { List } from './List';
import { Form } from './Form';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          id: 1,
          brand: "Ford",
          model: "Mustang"
        },
        {
          id: 2,
          brand: "Volvo",
          model: "Scoopy"
        }
      ]
    }

    this.clearList = this.clearList.bind(this);
    this.changeBrand = this.changeBrand.bind(this);
    this.addItem = this.addItem.bind(this);
  }

  clearList = () => {
    this.setState({ items: [] });
  }

  changeBrand = (event) => {
    let brand = event.target.value;
    const newItem = {
      id: Date.now(),
      brand: brand,
      model: "tes"
    }
    this.setState({ newItem });
  }

  addItem() {
    this.setState({ items: [...this.state.items, this.state.newItem] })
  }

  render() {
    return (
      <div className="App">
        <h2>Star Wars</h2>
        <List items={this.state.items} />
        <Form clearList={this.clearList} addItem={this.addItem} changeBrand={this.changeBrand} />
      </div>
    );
  }
}

export default App;
