import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import { List } from './List';
import { Form } from './Form';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      newItem: []
    }

    this.clearList = this.clearList.bind(this);
    this.onChangeInput = this.onChangeInput.bind(this);
    this.addItem = this.addItem.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }

  componentDidMount() {
    axios.get(`https://swapi.co/api/vehicles`)
      .then(res => {
        const result = res.data.results;
        let items = []
        for (let i = 0; i < 3; ++i) {
          items.push(result[i]);
        }
        this.setState({ items });
        console.log(this.state.items);
      })
  }

  clearList = () => {
    this.setState({ items: [] });
  }

  onChangeInput = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
    console.log(this.state.name);
  }

  addItem = () => {
    if(this.state.name && this.state.model) {
      const newItem = {
        id: Date.now(),
        name: this.state.name,
        model: this.state.model
      }
      this.setState({ items: [...this.state.items, newItem] })
    }
    else {
      alert('lengkapi form dulu')
    }
    console.log(this.state.items);
  }

  deleteItem = (id) => {
  this.setState({ 
    items: this.state.items.filter(el => el.id !== id ) 
  });
  console.log(this.state.items)
  }

  render() {
    return (
      <div className="App">
        <h2>Star Wars</h2>
        <Form clearList={this.clearList} addItem={this.addItem} onChangeInput={this.onChangeInput} deleteItem={this.deleteItem} />
        <List items={this.state.items} />
      </div>
    );
  }
}

export default App;
