import React from 'react';
import { Button } from './Button';

export class Form {
  state = {
    name: '',
    model: '',
  }

  onChangeInput = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
    console.log(this.state.name);
  }

  render () {
    return (
      <div>
        <form>
          <h4>{this.props.title}</h4>
          <label>Name:</label>
          <input type="text" placeholder="name" name="name" onChange={this.onChangeInput} />
          <label>Model:</label>
          <input type="text" placeholder="model" name="model" onChange={this.onChangeInput} />
        </form>
        <Button labelBtn="Save" handleBtn={() => this.props.addItem({ id: new Date(), ...this.state })} />
      </div>
    )
  }

}
