import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import { List } from './List';
import { Form } from './Form';
import { Button } from './Button';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
    }

    this.clearList = this.clearList.bind(this);
    this.onChangeInput = this.onChangeInput.bind(this);
    this.addItem = this.addItem.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }

  componentDidMount() {
    axios.get(`https://swapi.co/api/vehicles`)
      .then(res => {
        const result = res.data.results;
        const filteredResult = result.filter((val, idx) => idx < 3)
        const items = filteredResult.map((val, idx) => Object.assign(val, { id: idx }))

        this.setState({ items });
      })
  }

  clearList = () => {
    this.setState({ items: [] });
  }

  addItem = newItem => {
    if(this.state.name && this.state.model) {
      this.setState({ items: [...this.state.items, newItem] });
    }
    else {
      alert('lengkapi form dulu')
    }
    console.log(this.state.items);

  }

  deleteItem = itemId => {
    const items = this.state.items.filter(item => item.id !== itemId);
    this.setState({ items });
  };

  render() {
    return (
      <div className="App">
        <h2>Star Wars</h2>
        <Button labelBtn="Clear" handleBtn={this.clearList} />
        <Form title="Add new" addItem={this.addItem} onChangeInput={this.onChangeInput} />
        <List items={this.state.items} deleteItem={this.deleteItem} />
      </div>
    );
  }
}

export default App;
