import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Button } from './Button';

export class Form extends Component{

  constructor(props){
    super(props);
    this.state = {
      id: props.id,
      name: props.name || '',
      model: props.model || '',
    }
    this.onChangeInput = this.onChangeInput.bind(this);
    this.validateEmail = this.validateEmail.bind(this);
  }

  validateEmail(email){
    const pattern = /[a-zA-Z0-9]+[\.]?([a-zA-Z0-9]+)?[\@][a-z]{3,9}[\.][a-z]{2,5}/g;
    const result = pattern.test(email);
    if(result === true){
      this.setState({
        emailError:false,
        email:email
      })
    } else{
      this.setState({
        emailError:true
      })
    }
  }  
  min(inputName, inputValue, num){
    if(inputValue.length < num) {
      const inputError = inputName+'Error';
      this.setState({
        inputError: false
      })
    }
  }

  onChangeInput = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    let minLength = event.target.minLength;
    if(val.length < minLength) {
      console.log('kurang dari minimum');
    }
    this.setState({ [nam]: val });
  }

  render() {
    const { handleSubmit } = this.props
    return (
      <div>
        <form>
          <label>Name:</label>
          <input type="text" placeholder="name" name="name" minLength={5} value={this.state.name} onChange={this.onChangeInput} />
          <label>Model:</label>
          <input type="text" placeholder="model" name="model" minLength={5} value={this.state.model} onChange={this.onChangeInput} />
        </form>
        <Button labelBtn="Save" handleBtn={() => handleSubmit(this.state) } />
      </div>
    )
  }

}
Form.propTypes = {
  id: PropTypes.any,
  name: PropTypes.string,
  model: PropTypes.string,
  handleSubmit: PropTypes.func.isRequired
};

Form.defaultProps = {
  id: new Date()
};
